SELECT products.ProductName AS 'Produto', MIN(order.quantity) AS 'Mínima', MAX(order.quantity) AS 'Máxima', ROUND(AVG(order.quantity), 2) AS 'Média' FROM w3schools.order_details AS order INNER JOIN w3schools.products AS products ON order.ProductID = products.ProductID GROUP BY order.ProductID HAVING Média > 20.00 ORDER BY Média , Produto;
