SELECT products.ProductName AS 'Produto', MIN(details.Quantity) AS 'Mínima', MAX(details.Quantity) AS 'Máxima', ROUND(AVG(details.Quantity), 2) AS 'Média' FROM w3schools.order_details AS order INNER JOIN w3schools.products AS products ON order.ProductID = products.ProductID GROUP BY `Produto` HAVING `Média` > 20 ORDER BY `Média`, `Produto`;
