SELECT p.ProductName AS Produto, min(Quantity) AS 'quantidade mínima', max(Quantity) AS 'quantidade máxima', round(avg(Quantity), 2) AS 'média de quantidade' FROM w3schools.order_details od INNER JOIN w3schools.products p ON od.ProductID = p.ProductID GROUP BY Produto HAVING avg(Quantity) >= 20 ORDER BY `média de quantidade`, Produto;