select concat(x.FIRST_NAME, ' ', x.LAST_NAME) as 'Nome completo funcionário 1', x.SALARY as 'Salário funcionário 1', x.PHONE_NUMBER as 'Telefone funcionário 1', concat(y.FIRST_NAME, ' ', y.LAST_NAME) as 'Nome completo funcionário 2', y.SALARY as 'Salário funcionário 2', y.PHONE_NUMBER as 'Telefone funcionário 2' from hr.employees as x inner join hr.employees as y on x.JOB_ID = y.JOB_ID where concat(x.FIRST_NAME, ' ', x.LAST_NAME) <> concat(y.FIRST_NAME, ' ', y.LAST_NAME) order by 1, 4;