select concat(func_um.first_name, ' ', func_um.last_name) as 'Nome completo funcionário 1', func_um.salary as 'Salário funcionário 1', func_um.phone_number as 'Telefone funcionário 1', concat(func_dois.first_name, ' ', func_dois.last_name) as 'Nome completo funcionário 2', func_dois.salary as 'Salário funcionário 2', func_dois.phone_number as 'Telefone funcionário 2' from hr.employees as func_um, hr.employees as func_dois where func_um.JOB_ID = func_dois.JOB_ID and func_um.EMPLOYEE_ID <> func_dois.EMPLOYEE_ID order by `Nome completo funcionário 1`, `Nome completo funcionário 2`;