SELECT CONCAT(e.first_name, ' ', e.last_name) as 'Nome completo do funcionário 1', e.salary as 'Salário funcionário 1', e.phone_number as 'Telefone funcionário 1', CONCAT(e1.first_name, ' ', e1.last_name) as 'Nome completo do funcionário 2', e1.salary as 'Salário funcionário 2', e1.phone_number as 'Telefone funcionário 2' FROM hr.employees as e, hr.employees as e1 WHERE e.job_id = e1.job_id AND CONCAT(e.first_name, ' ', e.last_name) <> CONCAT(e1.first_name, ' ', e1.last_name) ORDER BY CONCAT(e.first_name, ' ', e.last_name), CONCAT(e1.first_name, ' ', e1.last_name);