SELECT concat(e1.first_name, ' ', e1.last_name) as 'Nome completo funcionário 1', e1.SALARY as 'Salário funcionário 1', e1.PHONE_NUMBER as 'Telefone funcionário 1', concat(e2.first_name, ' ', e2.last_name) as 'Nome completo funcionário 2', e2.SALARY as 'Salário funcionário 2', e2.PHONE_NUMBER as 'Telefone funcionário 2' FROM hr.employees e1, hr.employees e2 WHERE e1.job_id = e2.job_id and e1.EMPLOYEE_ID <> e2.EMPLOYEE_ID order by `Nome completo funcionário 1`, `Nome completo funcionário 2`;
