SELECT CONCAT(o.FIRST_NAME, ' ', o.LAST_NAME) 'Nome completo funcionário 1', o.SALARY 'Salário funcionário 1', o.PHONE_NUMBER 'Telefone funcionário 1', CONCAT(t.FIRST_NAME, ' ', t.LAST_NAME) 'Nome completo funcionário 2', t.SALARY 'Salário funcionário 2', t.PHONE_NUMBER 'Telefone funcionário 2'  FROM hr.employees o, hr.employees t WHERE o.JOB_ID = t.JOB_ID AND o.EMPLOYEE_ID <> t.EMPLOYEE_ID ORDER BY CONCAT(o.FIRST_NAME, ' ', o.LAST_NAME) , CONCAT(t.FIRST_NAME, ' ', t.LAST_NAME);
