DELIMITER $$ CREATE FUNCTION buscar_quantidade_de_empregos_por_funcionario(email VARCHAR(50)) RETURNS INT READS SQL DATA BEGIN DECLARE quantidade_empregos INT; SELECT COUNT(jb.job_id) FROM hr.employees AS e INNER JOIN hr.job_history AS jb ON e.employee_id = jb.employee_id WHERE e.email = email INTO quantidade_empregos; RETURN quantidade_empregos; END $$ DELIMITER ; SELECT buscar_quantidade_de_empregos_por_funcionario('NKOCHHAR');