select concat(e.first_name, ' ', e.last_name) as 'Nome Completo', concat(day(e.hire_date), '/', month(e.hire_date), '/', year(e.hire_date)) as 'Data de início', concat(day(jh.end_date), '/', month(jh.end_date), '/', year(jh.end_date)) as 'Data de rescisão', round(datediff(jh.start_date, jh.end_date) / 365, 2) as 'Anos trabalhados' from hr.employees as e inner join hr.job_history as jh on jh.EMPLOYEE_ID = e.EMPLOYEE_ID order by `Nome completo`, `Anos trabalhados` ; 