SELECT job_title as 'Cargo', case when MAX_SALARY >= 5000 and MAX_SALARY <= 10000 then 'Baixo' when MAX_SALARY >= 10001 and MAX_SALARY <= 20000 then 'Médio' when MAX_SALARY >= 20001 and MAX_SALARY <= 30000 then 'Alto' when MAX_SALARY >= 30001 then 'Altíssimo' end as 'Nível' FROM hr.jobs order by `Cargo`;
