/*FEITO COM A AJUDA DO ALUNO JOÃO OLIVEIRA*/
SELECT JOB_TITLE AS "Cargo", CASE WHEN MAX_SALARY between 5000 AND 10000 THEN "Baixo" WHEN MAX_SALARY between 10001 AND 20000 THEN "Médio" WHEN MAX_SALARY between 20001 AND 30000 THEN "Alto" WHEN MAX_SALARY > 30000 THEN "Altíssimo" ELSE "Não incluido" END AS "Nível" FROM hr.jobs order by `Cargo`;