select job_title as 'Cargo', round(avg(funcionario.salary), 2) as 'Média salarial', case when round(avg(funcionario.salary), 2) between  2000 and  5800 then 'Júnior' when round(avg(funcionario.salary), 2) between 5801 and  7500 then 'Pleno' when round(avg(funcionario.salary), 2) between  7501 and 10500 then 'Sênior' else 'CEO' end as 'Senioridade' from hr.jobs as trabalho inner join hr.employees as funcionario on funcionario.job_id = trabalho.JOB_ID group by trabalho.job_id order by avg(funcionario.salary), Cargo;