SELECT r.Cargo, r.media as 'Média salarial', case when r.media >= 2000 and r.media <= 5800 then 'Júnior' when r.media >= 5801 and r.media <= 7500 then 'Pleno' when r.media >= 7501 and r.media <= 10500 then 'Sênior' when r.media > 10500 then 'CEO' end as Senioridade FROM (SELECT j.JOB_TITLE AS Cargo, round(AVG(e.salary), 2) AS media FROM hr.employees AS e join hr.jobs as j on j.JOB_ID = e.JOB_ID GROUP BY Cargo) as r ORDER BY r.media, r.Cargo;
