SELECT j.JOB_TITLE AS Cargo, round(avg(e.SALARY),2) AS 'Média salarial', CASE WHEN avg(e.SALARY) >= 2000 AND avg(e.SALARY) <= 5800 THEN 'Júnior' WHEN avg(e.SALARY) >= 5801 AND avg(e.SALARY) <= 7500 THEN 'Pleno' WHEN avg(e.SALARY) >= 7501 AND avg(e.SALARY) <= 10500 THEN 'Sênior' ELSE 'CEO' END AS Senioridade FROM hr.employees e INNER JOIN hr.jobs j ON j.JOB_ID = e.JOB_ID GROUP BY j.JOB_TITLE ORDER BY `Média salarial`, Cargo;