select jobs.JOB_TITLE as 'Cargo', round(avg(employees.SALARY), 2) as 'Média salarial', case when round(avg(employees.SALARY), 2) between 2000 and 5800 then 'Júnior' when round(avg(employees.SALARY), 2) between 5801 and 7500 then 'Pleno' when round(avg(employees.SALARY), 2) between 7501 and 10500 then 'Sênior' else 'CEO' end as 'Senioridade' from hr.jobs as jobs inner join hr.employees as employees on employees.JOB_ID = jobs.JOB_ID group by JOB_TITLE order by round(avg(employees.SALARY), 2) asc, JOB_TITLE asc;