SELECT CONCAT(e.FIRST_NAME,' ',e.LAST_NAME) 'Nome completo', j.JOB_TITLE 'Cargo', jh.START_DATE 'Data de in√≠cio do cargo', d.DEPARTMENT_NAME 'Departamento' FROM hr.job_history AS jh INNER JOIN hr.employees AS e INNER JOIN hr.jobs AS j INNER JOIN hr.departments AS d ON e.EMPLOYEE_ID = jh.EMPLOYEE_ID and jh.JOB_ID = j.JOB_ID AND jh.DEPARTMENT_ID = d.DEPARTMENT_ID ORDER BY `Nome Completo` DESC, `Cargo`;
