SELECT CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) AS 'Nome completo', j.JOB_TITLE AS Cargo, h.START_DATE AS 'Data de in√≠cio do cargo', d.DEPARTMENT_NAME AS Departamento FROM hr.jobs j INNER JOIN  job_history h ON j.JOB_ID = h.JOB_ID INNER JOIN departments d ON h.DEPARTMENT_ID = d.DEPARTMENT_ID INNER JOIN employees e ON e.EMPLOYEE_ID = h.EMPLOYEE_ID ORDER BY CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) DESC , j.JOB_TITLE;
