SELECT CONCAT(e.FIRST_NAME, ' ' , e.LAST_NAME) `Nome completo`, jo.JOB_TITLE 'Cargo', j.START_DATE 'Data de in√≠cio do cargo', d.DEPARTMENT_NAME 'Departamento' FROM hr.job_history j INNER JOIN hr.employees e ON j.EMPLOYEE_ID = e.EMPLOYEE_ID INNER JOIN hr.jobs jo ON j.JOB_ID = jo.JOB_ID INNER JOIN hr.departments d ON j.DEPARTMENT_ID = d.DEPARTMENT_ID ORDER BY `Nome completo` DESC, Cargo;