SELECT CONCAT(e.first_name, ' ', e.last_name) AS 'Nome completo', j.job_title AS 'Cargo', h.start_date AS 'Data de in√≠cio do cargo', d.department_name AS 'Departamento' FROM employees AS e INNER JOIN job_history AS h ON e.employee_id = h.employee_id INNER JOIN jobs AS j ON j.job_id = h.job_id INNER JOIN departments AS d ON h.department_id = d.department_id ORDER BY CONCAT(e.first_name, ' ', e.last_name) DESC, j.job_title;