SELECT CONCAT(e.first_name, ' ', e.last_name) AS 'Nome completo', j.JOB_TITLE as 'Cargo', h.start_date as 'Data de in√≠cio do cargo', d.department_name as 'Departamento' FROM hr.job_history AS h INNER JOIN hr.employees AS e  ON e.EMPLOYEE_ID = h.EMPLOYEE_ID INNER JOIN hr.jobs as j ON h.job_id = j.job_id INNER JOIN hr.departments as d ON h.department_id = d.department_id ORDER BY CONCAT(e.first_name, ' ', e.last_name) DESC, j.job_title;