select concat(funcionarios.first_name, " ", funcionarios.last_name) as 'Nome completo', trabalho.JOB_TITLE as 'Cargo', inicio.START_DATE as 'Data de in√≠cio do cargo', departamento.department_name as 'Departamento' from hr.employees as funcionarios inner join hr.jobs as trabalho on funcionarios.job_id = trabalho.job_id inner join hr.departments as departamento on departamento.department_id = funcionarios.department_id inner join hr.job_history as inicio on inicio.EMPLOYEE_ID = funcionarios.EMPLOYEE_ID order by concat(funcionarios.first_name, " ", funcionarios.last_name desc, trabalho.JOB_TITLE;