SELECT DISTINCT UPPER(CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME)) AS 'Nome completo', h.START_DATE AS 'Data de início', e.SALARY AS Salário FROM hr.jobs j INNER JOIN job_history h ON j.JOB_ID = h.JOB_ID AND (h.START_DATE LIKE '%-01-%') OR (h.START_DATE LIKE '%-02-%') OR (h.START_DATE LIKE '%-03-%') INNER JOIN departments d ON h.DEPARTMENT_ID = d.DEPARTMENT_ID INNER JOIN employees e ON e.EMPLOYEE_ID = h.EMPLOYEE_ID ORDER BY CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) , h.START_DATE ;
