select UCASE(CONCAT(employees.FIRST_NAME, ' ', employees.LAST_NAME)) as 'Nome completo' hist.START_DATE as 'Data de início', employees.SALARY as 'Salário' from hr.employees as employees inner join hr.job_history as hist on hist.EMPLOYEE_ID = employees.EMPLOYEE_ID where MONTH(hist.START_DATE) in (1, 2, 3) order by `Nome completo` asc, hist.START_DATE asc;