SELECT c.contactname as 'Nome do contato', s.shippername as 'Empresa que fez o envio', o.orderdate as 'Data do pedido' FROM orders as o INNER JOIN customers as c ON o.customerid = c.customerid INNER JOIN shippers as s ON o.shipperid = s.shipperid WHERE o.shipperid IN (1, 2) ORDER BY c.contactname, o.orderdate;