SELECT c.ContactName AS 'Nome de contato', s.ShipperName AS 'Empresa que fez o envio', o.orderDate AS 'Data do pedido' FROM w3schools.orders o INNER JOIN customers c ON c.CustomerID = o.CustomerID INNER JOIN shippers s ON s.ShipperID = o.ShipperID AND (s.ShipperName = 'Speedy Express' OR s.ShipperName = 'United Package') ORDER BY c.ContactName, s.ShipperName, o.OrderDate
