SELECT c.contactName AS 'Nome de contato', s.shipperName AS 'Empresa que fez o envio', o.OrderDate AS 'Data do pedido' FROM w3schools.customers AS c INNER JOIN w3schools.orders AS o ON o.CustomerID = c.CustomerID INNER JOIN w3schools.shippers AS s ON o.ShipperID = s.ShipperID WHERE s.shipperName = 'Speedy Express' OR s.shipperName = 'United Package' ORDER BY c.contactName, o.OrderDate;