SELECT c.ContactName 'Nome de contato', s.ShipperName 'Empresa que fez o envio', o.OrderDate 'Data do pedido' FROM w3schools.customers AS c INNER JOIN w3schools.orders AS o INNER JOIN w3schools.shippers AS s ON s.ShipperID = o.ShipperID AND o.CustomerID = c.CustomerID WHERE (s.ShipperID = 1 OR s.ShipperID = 2) ORDER BY `Nome de contato`, `Empresa que fez o envio`, `Data do pedido`;
